import React, { useState } from 'react';
import * as S from './ExpenseForm.style';

const ExpenseForm = () => {
    const [title, setTitle] = useState('title');
    const [amount, setAmount] = useState(0);
    const [date, setDate] = useState(null);

    const changeHandler = (event) => {
        const { value, name} = event.target;
        console.log(value, name);
    }
    return (
        <S.MainWrapper>
        <S.Form>
            <S.Main>
            <S.InputWrapper>
            <S.Label>Title</S.Label>
            <S.FormInput type='text' onChange={changeHandler}/>
            </S.InputWrapper>
            <S.InputWrapper>
            <S.Label>Amount</S.Label>
            <S.FormInput type='number' onChange={changeHandler}/>
            </S.InputWrapper>
            </S.Main>
            <S.Main>
            <S.InputWrapper>
            <S.Label>Date</S.Label>
            <S.FormInput type='date' onChange={changeHandler}/>
            </S.InputWrapper>
            </S.Main>
            <S.ButtonWrapper>
            <S.Button type='submit'>Add Expense</S.Button>
            </S.ButtonWrapper>
        </S.Form>
        </S.MainWrapper>
    );
}

export default ExpenseForm;
