import styled from 'styled-components';

export const Button = styled.button`
    color: white;
    padding: 10px;
    background-color: #a11ec9;
    font-family: 'proxima-nova';
    border: 1px solid #a11ec9;
    border-radius: 5px;
    font-size: 1rem;
    cursor: pointer;
    width: 15%;
    max-width: 40rem;
    margin: 2rem 0;
    margin-left: 44%;
    &:hover {
        background-color: #2f0a3b;
        cursor: pointer;
    }
`;
